(3, 1, 1, 0, 0, 'Orientaciones Generales sobre la elaboración, presentación y evaluación de proyectos de obras escritas para ascenso en el escalafon docente', 'orientacionesevescritas', 'calidadeduca/orientacion_sobre_obras_escritas.pdf', 209739, 'calidadeduca/orientacion_sobre_obras_escritas.pdf', 'calidadeduca/orientacion_sobre_obras_escritas.pdf', '', '', '', '', '', '', '', '', '', '', '', '', 0, '2011-05-24 15:07:09', '2011-05-24 15:07:09', '0000-00-00 00:00:00', 2256, 0, 1, 1, 0, '0000-00-00 00:00:00', 2, 0, -1, 0, NULL, '', ''),
(4, 2, 1, 0, 0, 'Requisitos para ascenso de docentes regidos por el decreto 2277 de 1979  ', 'ascenso2277', 'escalafon/requisitosascensos.pdf', 17141, 'escalafon/requisitosascensos.pdf', 'escalafon/requisitosascensos.pdf', '', '', '', '', '', '', '', '', '', '', '<p style="text-align: justify;">El horario de atención establecido para entregar requisitos es Martes y Jueves de 8 a 10 a.m y de 2 a 4 p.m en la oficina de Servicio de atención al ciudadano SAC de la Secretaría de Educación Municipal.</p>\r\n<p style="text-align: justify;">Para la solicitud de ascenso en el escalafón, debe diligenciar el formato y adjuntar la documentación.</p>\r\n<p> </p>', '', 0, '2011-05-24 15:12:55', '2011-05-24 15:12:55', '0000-00-00 00:00:00', 654, 0, 1, 1, 0, '0000-00-00 00:00:00', 1, 0, -1, 0, NULL, '', ''),
